tek_sayilar=[3,5,7,9,11,13,15,17,19] #Liste oluşturuldu
print(tek_sayilar[0:6]) #İndeksi 0 (dahil değil) dan başlandı ve indeksi 6 olan elemana kadar yazdırıldı
print(tek_sayilar[2:5]) #İndeksi 2 (dahil değil) olan elemandan başlandı ve indeksi 5 olan elemana kadar yazdırıldı.
print(tek_sayilar[3:8]) #İndeksi 3 (dahil değil) olan elemandan başlandı ve indeksi 8 olan elemana kadar yazdırıldı
print(tek_sayilar[:5])  #Listenin başlangıcından, indeks numarası 5 olan elemana kadar yazdırıldı.
print(tek_sayilar[3:])  #İndeksi 3 (dahil değil) olan elemandan başlandı ve listenin sonuna kadar yazdırıldı.
print(tek_sayilar[0:8:2]) #İndeksi 0 (dahil değil) olan elemandan başlandı ve indeksi 8 olan elemana kadar ikişer atlayarak yazdırıldı
print(tek_sayilar[::3]) #Listenin başlangıcından sonuna kadar üçer atlayarak yazdırıldı.